import React from "react";
import styled from "styled-components";

const Box = styled.div`
  width: 400px;
  min-width: 400px;
  height: 230px;
  background-color: #999;

  margin: 25px;

  border-radius: 7px;
  box-shadow: 2px 2px 2px #8383835b;
  padding: 5px;

  display: flex;
  flex-direction: row;
`;

const PosterDiv = styled.div`
  width: 120px;
  height: 100%;
`;
const ContentDiv = styled.div`
  width: calc(100% - 120px);
  height: 100%;
`;

const PosterImg = styled.Img`
  width: 120px;
  object-fit: cover;

  margin-top: -20px;
  margin-left: -20px;

  border-radius: 7px;

  box-shadow: 2px 2px 2px #8383835b;
`;

const MovieBox = () => {
  return (
    <Box>
      <PosterDiv>
        <PosterImg />
      </PosterDiv>
      <ContentDiv>C</ContentDiv>
    </Box>
  );
};

export default MovieBox;
