import React from "react";
import styled from "styled-components";

const Box = styled.div`
  width: 400px;
  min-width: 400px;
  height: 230px;
  background-color: #99999941;

  margin: 25px;

  border-radius: 7px;
  box-shadow: 2px 2px 2px #8383835b;
  padding: 5px;

  display: flex;
  flex-direction: row;
`;

const PosterDiv = styled.div`
  width: 120px;
  height: 100%;
`;
const ContentDiv = styled.div`
  width: calc(100% - 120px);
  height: 100%;
`;

const PosterImg = styled.img`
  width: 140px;
  object-fit: cover;

  margin-top: -20px;
  margin-left: -20px;

  border-radius: 7px;

  box-shadow: 2px 2px 2px #8383835b;
`;

const Title = styled.div`
  font-weight: 700;
  margin-bottom: 5px;
`;

const Vote = styled.div`
  font-size: 12.5px;
  color: #505050;

  margin-bottom: 25px;
`;

const Overview = styled.div`
  word-break: break-all;
`;

const MovieBox = ({ title, vote, over, posterPath }) => {
  return (
    <Box>
      <PosterDiv>
        <PosterImg src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSbWyGpM5JjGrjqOi4PcA9_1EnmGTVxhpcABQ&usqp=CAU" />
      </PosterDiv>
      <ContentDiv>
        <Title> ðŸ”¹ {title} </Title>
        <Vote> {Vote}</Vote>
        <Overview>
          {over.length > 100 ? over.substring(0, 100) + "..." : over}
        </Overview>
      </ContentDiv>
    </Box>
  );
};

export default MovieBox;
